    var sum = a;
    var s = function(b) {
        sum += b;
        //s，继续累加后面的执行参数。
        return s;
    };
    //当要打印一个对象时，会自动调用 valueOf()或 toString方法
    s.toString = function() {
        return sum;
    };
    //获取第一个参数赋值给sum后，s。
    return s;
};

