       alert(xhr.respenseText);
   }else{
       alert("获取数据错误  错误代号："+xhr.status+",错误信息："+xhr.statusText);
   }
});

```
-  ### 异步

```
addEvent(document,"click",function(){
   var xhr=createXHR();
   xhr.onreadystatechange=function(){
       if(xhr.readyState==4){
           if(xhr.status==200){
               alert(xhr.respenseText);
